CREATE DEFINER=`root`@`localhost` PROCEDURE `CreateCafeOrder`(
 
    IN p_cafe_id INT,
    IN p_route_id INT,
    IN p_order_date DATETIME,
    IN p_total_amount FLOAT,
    IN p_tax FLOAT,
    IN p_delivery_charges FLOAT,
    IN p_payment_status BOOLEAN,
    IN p_note VARCHAR(255),
    IN p_delivery_status boolean,
    IN p_cafe_invoice_id int,
    IN p_cafe_order_delivery_id int,
    OUT order_id INT,
    OUT p_code VARCHAR(255),
    OUT order_number varchar(255)
)
BEGIN
    -- Declare variables for order number and cafe code
    DECLARE p_order_number VARCHAR(255);
    DECLARE p_code VARCHAR(255);
 
    -- Fetch the cafe code from tbl_cafes using the provided cafe_id
    SELECT code 
    INTO p_code 
    FROM tbl_cafes
    WHERE cafe_id = p_cafe_id 
    LIMIT 1;
 
    INSERT INTO tbl_cafe_order(
        cafe_id, 
        route_id, 
        order_date, 
        total_amount, 
        tax, 
        delivery_charges, 
        payment_status, 
        note,
        delivery_status,
        cafe_invoice_id,
        cafe_order_delivery_id
    )
    VALUES (
        p_cafe_id, 
        p_route_id, 
        p_order_date, 
        p_total_amount, 
        p_tax, 
        p_delivery_charges, 
        p_payment_status, 
        p_note,
        p_delivery_status,
        p_cafe_invoice_id,
        p_cafe_order_delivery_id
    );
 
    SET order_id = LAST_INSERT_ID();
    SET p_order_number = CONCAT('PO', p_code, order_id);
 
    UPDATE tbl_cafe_order
    SET order_number = p_order_number
    WHERE cafe_order_id = order_id; 
      SET order_number = p_order_number;
END ;;
DELIMITER ;
