CREATE DEFINER=`root`@`localhost` PROCEDURE `CreateInvoice`(
IN ci_cafe_id int,
IN ci_cafe_order_id int,
IN ci_invoice_date datetime,
IN ci_total_amount varchar(255),
IN ci_tax float,
IN ci_discount float,
In ci_payment_id int,
IN ci_payment_term_id int,
IN ci_invoice_due_date datetime,
IN ci_created_by int,
OUT invoices_id int,
OUT p_code VARCHAR(255),
OUT invoice_number varchar(255)
)
BEGIN
  -- Declare variables for order number and cafe code
    DECLARE ci_invoice_number VARCHAR(255);
    DECLARE p_code VARCHAR(255);
 
    -- Fetch the cafe code from tbl_cafes using the provided cafe_id
    SELECT code 
    INTO p_code 
    FROM tbl_cafes
    WHERE cafe_id = ci_cafe_id 
    LIMIT 1;
 
	INSERT INTO tbl_cafe_invoices(
        cafe_id, 
        cafe_order_id, 
        invoice_date, 
        total_amount, 
        tax, 
        discount, 
        payment_id, 
        payment_term_id, 
        invoice_due_date, 
        created_by
    )
    VALUES (
        ci_cafe_id, 
        ci_cafe_order_id, 
        ci_invoice_date, 
        ci_total_amount, 
        ci_tax, 
        ci_discount, 
        ci_payment_id, 
        ci_payment_term_id, 
        ci_invoice_due_date, 
        ci_created_by
    );

    SET invoices_id = LAST_INSERT_ID();
	SET ci_invoice_number = CONCAT('INV', p_code, invoices_id);
 
    UPDATE tbl_cafe_invoices
    SET invoice_number = ci_invoice_number
    WHERE cafe_invoices_id = invoices_id; 
    
     -- Set the OUT parameter to return the generated invoice number
	 SET  invoice_number = ci_invoice_number;

END ;;
