CREATE DEFINER=`root`@`localhost` PROCEDURE `GetAllCafeOrders`()
BEGIN
    SELECT 
        co.cafe_order_id,
        tbl_cafes.cafe_id,
        tbl_cafes.name AS cafe_name,
        tbl_cafes.address,
        co.route_id,
        co.order_number,
        co.order_date,
        co.total_amount,
        co.tax,
        co.delivery_charges,
        co.payment_status,
        co.note,
        co.delivery_status,
        co.cafe_invoice_id,
        co.cafe_order_delivery_id,
        JSON_ARRAYAGG(
            JSON_OBJECT(	
                'product_id', tbl_products.product_id,
				'name', tbl_products.name,
                'price_scale', tbl_products.price_scale,
                'description', cod.description, 
                'quantity', cod.quantity, 
                'rate', cod.rate, 
                'sub_total_amount', cod.sub_total_amount
            )
        ) AS products
    FROM 
        tbl_cafe_order co
    LEFT JOIN 
        tbl_cafe_order_details cod ON co.cafe_order_id = cod.cafe_order_id
	INNER JOIN 
		tbl_cafes ON co.cafe_id = tbl_cafes.cafe_id
	inner join 
      tbl_products On cod.product_id  = tbl_products.product_id
	
    GROUP BY 
        co.cafe_order_id
	ORDER BY 
        co.cafe_order_id DESC;
END ;;
DELIMITER ;
