CREATE DEFINER=`root`@`localhost` PROCEDURE `GetAllInvoice`()
BEGIN
	select 
   tbl_cafes.cafe_id,
   tbl_cafes.payment_terms_id,
   tbl_cafes.name,
   co.cafe_order_id,
   co.payment_status,
   ci.invoice_number,
   ci.invoice_date,
   ci.total_amount,
   ci.tax,
   ci.delivery_charges,
   ci.discount,
   ci.payment_id,
   ci.payment_date,
   ci.note,
   ci.invoice_due_date,
     JSON_ARRAYAGG(
            JSON_OBJECT(	
                'cafe_invoice_details_id', cid.cafe_invoice_details_id,
				'product_id', cid.product_id,
				'quantity',  cid.quantity,
                'rate', cid.rate,
                'sub_total_amount', cid.sub_total_amount,
                'description', cid.description
            )
        ) AS products
    
    from tbl_cafe_invoices ci
    INNER JOIN 
		tbl_cafes ON ci.cafe_id = tbl_cafes.cafe_id
    INNER JOIN
		tbl_cafe_order co ON ci.cafe_order_id = co.cafe_order_id
    INNER JOIN 
		tbl_cafe_payments cp ON ci.payment_id = cp.cafe_payments_id
	LEFT JOIN 
		tbl_cafe_invoice_details cid ON ci.cafe_invoices_id = cid.cafe_invoices_id
        
		GROUP BY 
        ci.cafe_invoices_id
		ORDER BY 
        ci.cafe_invoices_id DESC;
END ;;
DELIMITER ;
